mapper.select:
select
count(1)
from tb_product_attribute
where name = #{name,jdbcType=VARCHAR} and product_attribute_category_id = #{productAttributeCategoryId,jdbcType=INTEGER}

  and id != #{id,jdbcType=INTEGER}

select
count(1)
from tb_product_attribute
where product_attribute_category_id = #{attributePageVO.productAttributeCategoryId,jdbcType=INTEGER}
and type =  #{attributePageVO.type,jdbcType=INTEGER}

select
  a.id, a.product_attribute_category_id, a.name, a.select_type, a.input_type, a.input_list, a.sort, a.type, b.name AS attributeCategoryName
from tb_product_attribute a
left join tb_product_attribute_category b on a.product_attribute_category_id = b.id
where  a.product_attribute_category_id = #{attributePageVO.productAttributeCategoryId,jdbcType=INTEGER}
and type =  #{attributePageVO.type,jdbcType=INTEGER}
LIMIT #{attributePageVO.dbIndex,jdbcType=INTEGER},#{attributePageVO.dbNumber,jdbcType=INTEGER}

