mapper.select:
select
count(1)
from tb_product_category
where name = #{name,jdbcType=VARCHAR}

  and id != #{id,jdbcType=INTEGER}

select a.id, a.parent_id, a.name, a.code, a.show_status, a.sort, a.icon, a.keywords, a.description, a.create_id, a.create_time, a.update_id, a.update_time, b.name as parentName from tb_product_category a left join tb_product_category b on a.parent_id = b.id where a.show_status != 2
and a.name LIKE concat(concat('%',#{productCategoryPageVO.name,jdbcType=VARCHAR}),'%')
and a.code LIKE concat(concat('%',#{productCategoryPageVO.code,jdbcType=VARCHAR}),'%')
and a.show_status = #{productCategoryPageVO.showStatus,jdbcType=INTEGER}
ORDER BY a.id DESC LIMIT #{productCategoryPageVO.dbIndex,jdbcType=INTEGER},#{productCategoryPageVO.dbNumber,jdbcType=INTEGER}
