mapper.select:
select count(1) from tb_product where delete_status != 1
and name LIKE concat(concat('%',#{productPageVO.name,jdbcType=VARCHAR}),'%')
and product_sn LIKE concat(concat('%',#{productPageVO.productSn,jdbcType=VARCHAR}),'%')
and product_category_id = #{productPageVO.productCategoryId,jdbcType=INTEGER}
and publish_status = #{productPageVO.publishStatus,jdbcType=INTEGER}

SELECT a.id, a.product_category_id, a.name, a.pic, a.product_sn, a.publish_status,
a.new_status, a.recommand_status, a.sort, a.sale, a.price,b.name FROM tb_product a
LEFT JOIN tb_product_category b ON a.product_category_id = b.id
where  a.delete_status != 1

  and a.name LIKE concat(concat('%',#{productPageVO.name,jdbcType=VARCHAR}),'%')


  and a.product_sn LIKE concat(concat('%',#{productPageVO.productSn,jdbcType=VARCHAR}),'%')


  and a.product_category_id = #{productPageVO.productCategoryId,jdbcType=INTEGER}


  and a.publish_status = #{productPageVO.publishStatus,jdbcType=INTEGER}

ORDER BY a.id DESC
LIMIT #{productPageVO.dbIndex,jdbcType=INTEGER},#{productPageVO.dbNumber,jdbcType=INTEGER}
