select count(1) from tb_user where status != 2
and user_name LIKE concat(concat('%',#{userPageVo.userName,jdbcType=VARCHAR}),'%')
and account LIKE concat(concat('%',#{userPageVo.account,jdbcType=VARCHAR}),'%')
and phone LIKE concat(concat('%',#{userPageVo.phone,jdbcType=VARCHAR}),'%')
and type = #{userPageVo.type,jdbcType=INTEGER}
and status = #{userPageVo.status,jdbcType=INTEGER}

select

from tb_user
where status != 2

  and user_name LIKE concat(concat('%',#{userPageVo.userName,jdbcType=VARCHAR}),'%')


  and account LIKE concat(concat('%',#{userPageVo.account,jdbcType=VARCHAR}),'%')


  and phone LIKE concat(concat('%',#{userPageVo.phone,jdbcType=VARCHAR}),'%')


  and type = #{userPageVo.type,jdbcType=INTEGER}


  and status = #{userPageVo.status,jdbcType=INTEGER}

ORDER BY create_time DESC
LIMIT #{userPageVo.dbIndex,jdbcType=INTEGER},#{userPageVo.dbNumber,jdbcType=INTEGER}

select count(1) from tb_user
where account = #{account,jdbcType=VARCHAR}

  and id != #{id,jdbcType=INTEGER}

select count(1) from tb_user
where phone = #{phone,jdbcType=VARCHAR}

  and id != #{id,jdbcType=INTEGER}
